import React from 'react';
import todosService from '../services/todos.services';

export default class TodosApp extends React.PureComponent{
    state = {
        newTodoContent:"",
        todos: []
    }

    componentDidMount(){
        todosService.listTodos()
        .then(todos => this.setState({ todos }))
        .catch(console.error.bind(console))
    }
    render(){
        const { todos, newTodoContent } = this.state;
        return (
            <div className="todos-root">
                <h1>Todos</h1>
               <div className="todos-header">
                   <input type="checkbox"/>
                   <input type="text" placeholder="What needs to be done ? " 
                   onChange={e => this.setState({NewTodoContent: e.target.value})}/>
               </div>
               <ul className="todos-list">
                   <li>{newTodoContent}</li>
                   {todos.map(todo => 
                    <li key={`todo_${todo._id}`} className="todo-item">
                        <input type="checkbox"/>
                        <span className="todo-content">{todo.content}</span>
                    </li>
                   )}
               </ul>
            </div>
        );
    }
}
