const API_URL = "http://localhost:3000";

export default{
    listTodos
}

function apiFetch(path, options){
    return fetch(`${API_URL}${path}`, options);
}

function listTodos(){
    return apiFetch("/todos")
    .then(response => response.json());
}

function createTodo(content){
    return apiFetch("/todos", {
        method: 'POST',
    body: {content}
    });
}
