import { bindActionCreators } from "redux";

const  initState = {
    list : []
}
export default function(state = initState, action){
    
    switch(action.type){
        case 'LIST_TODOS_SUCCESS':
            return{
                list: action.todos 
            }
        default:
         return state ;
    }
}
