import React from 'react';

export default

class TodoItem extends React.PureComponent{
    render(){
        const { todo } = this.props
        return( 
            <li className="todo-item">
                <input type="checkbox"/>
                <span className="todo-content">{todo.content}</span>
                <button
                onClick={this.handleDelete}> X
                    </button>
             </li>
        );
    }
}
